import React, { Component } from 'react';
import {url} from '../../urlBase';
import axios from 'axios';

class AdminSummary extends Component {
    state = {
        summary: []
    }

    async componentDidMount() {
        let mySummary = await axios({
            url: `${url}api/v1/summaries`,
            method: 'GET',
            headers: {
                authorization: `Bearer ${localStorage.token}`
            }});

        this.setState({
            summary: mySummary.data
        })

    }

    handleCaption = (event) => {
        this.setState({
            summary: event.target.value
        })
    }

    submitCaption = (event) => {
        event.preventDefault();
        axios({
            url: `${url}api/v1/summaries`,
            method: 'POST',
            headers: {
                authorization: `Bearer ${localStorage.token}`
            },
            data: {
                content: this.state.summary.content,
                user_id: this.state.summary.user_id
            }
        })
            .then(result => console.log(result));
    }

    render() {
        console.log("AdminSummary: ", this.state.summary);
        return (
            <div className="admin-caption">
                <div className="admin-caption-header">
                    <h2>About Me</h2>
                </div>
                <form onSubmit={this.submitCaption}>
                    <textarea name="bio" onChange={this.handleCaption} value={this.state.summary} placeholder="Tell them what you're about." />
                    <button>Submit</button> 
                </form>               
            </div>
        )
    }
}

export default AdminSummary;
