import React, { Component } from 'react';
import {url} from '../../urlBase';
import axios from 'axios';

class AdminSkills extends Component {
    state = {
        skills: [],
        skillsForm: false
    }

    async componentDidMount() {
        await axios({
            url: `${url}api/v1/users`,
            method: 'GET',
            headers: {
                authorization: `Bearer ${localStorage.token}`
            }})
            .then(res => {
                this.setState({
                    skills: res.data[0].skills
                })
            })
    }

    skillsFormToggle = () => {
        this.setState({
            skillsForm: !this.state.skillsForm
        })
    }

    handleInput = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
}

    render() {
        return (
            <div className="admin-skills">
                <div className="admin-skills-header">
                    <h2>Skills</h2>
                    <button onClick={this.skillsFormToggle}>+</button> 
                </div>
                <ul className="admin-skills-list">
                    {this.state.skillsForm ?
                        <li>
                            <form>
                                <input name="skillname" type="text" placeholder="Skill Name" />
                                <input name="level" type="number" placeholder="Level" />
                                <button>Add</button>
                            </form>
                        </li> 
                        : null}

                    <li>
                        <h3>ReactJS</h3>
                        <p>65%</p>
                    </li>
                    <li>
                        <h3>JavaScript</h3>
                        <p>75%</p>
                    </li>
                </ul>     
            </div>
        )
    }
}

export default AdminSkills;
