import React from 'react';
import '../../css/client_css/Skill.scss';

const Skill = (props) => {
    console.log("props: ", props.skill.name);
    const {name, level} = props.skill;
    const barLength = `${level}%`;

    const skillSection = document.querySelector('#skills');
    const iObserver = new IntersectionObserver(entries => {
      console.log("entries: ", entries[0].isIntersecting);
      if (!entries[0].isIntersecting) {
        const divStyle = {
            height: '100%',
            width: barLength,
            transitionDuration: '2s'
        };
      } else {
        const divStyle = {
            height: '100%',
            width: '0%',
            transitionDuration: '2s'
        };
      }
    });

    iObserver.observe(skillSection);

    return (
        <div class="skill">
            <h3>{name}</h3>
            <div className="skill-meter">
                <div className="skill-level" style={divStyle}></div>
            </div>
        </div>

    );
};
export default Skill;
