import React, { Component } from 'react';
import {url} from '../../urlBase';
import axios from 'axios';

class AdminSummary extends Component {
    state = {
        summary: ''
    }

    async componentDidMount() {
        let mySummary = await axios({
            url: `${url}api/v1/summaries`,
            method: 'GET',
            headers: {
                authorization: `Bearer ${localStorage.token}`
            }});

        if (mySummary.data.length > 0) {
            this.setState({
                summary: mySummary.data
            })
        }       
    }

    handleCaption = (event) => {
        this.setState({
            summary: event.target.value
        })
    }

    submitCaption = (event) => {
        event.preventDefault();
        let id = localStorage.getItem("id");

        axios({
            url: `${url}api/v1/summaries/${id}`,
            method: 'PATCH',
            headers: {
                authorization: `Bearer ${localStorage.token}`
            },
            data: {
                content: this.state.summary,
                user_id: id
            }
        })
            .then(result => console.log(result));
    }

    render() {
        let content;
        if (this.state.summary.length > 0) {
            content = this.state.summary[0].content;
        } else {
            content = '';
        }
        console.log("AdminSummary: ", content);
        return (
            <div className="admin-caption">
                <div className="admin-caption-header">
                    <h2>About Me</h2>
                </div>
                <form onSubmit={this.submitCaption}>
                    <textarea name="bio" onChange={this.handleCaption} value={content} placeholder="Tell them what you're about." />
                    <button>Submit</button> 
                </form>               
            </div>
        )
    }
}

export default AdminSummary;
